import React, {Component} from "react";
import {Link} from "react-router-dom";

class SignUp extends Component {

    constructor(props) {
        super();

        this.state = {
            first_name: "",
            last_name: "",
            username: "",
            password: "",
            user_type:""
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange(event) {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        })
    }

    async handleSubmit(event) {
        event.preventDefault(); //why
        event.stopPropagation(); //why

        let response = await fetch('/employee/registerEmployee', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'Accept': '*/*'
            },

            body: JSON.stringify({
                first_name: this.state.first_name,
                last_name: this.state.last_name,
                password: this.state.password,
                username: this.state.username,
                user_type:this.state.user_type,
            })
        });
        let status = response.status;
        if (status === 200) {
            this.props.history.push({
                pathname: '/DashBoard',
                customer: await response.json()
            });
        } else if (status === 404) {
            this.setState({
                errorMessage: true
            })
        } else {
            this.props.history.push({
                pathname: '/Error404',
                message: 'Backend server is down'
            });
        }
    }

    render() {
        return (
            <div className="signup">
                <body>
                    <div className="register">
                        <h1>Create an account</h1>
                        <p>Already have an account?<Link to="/SignIn"> Sign in</Link></p>
                    </div>

                    <div className="main">
                        <form onSubmit={this.handleSubmit}>
                            <div id="name">
                                <h2 className="name">Name</h2>
                                <input
                                    type="text"
                                    name="first_name"
                                    required="True"
                                    className="firstName"
                                    placeholder="First Name"
                                    value={this.state.first_name}
                                    onChange={this.handleChange}
                                />
                                <br/>
                                <label className="firstLabel">First Name: </label>
                                <input
                                    type="text"
                                    name="last_name"
                                    className="lastName"
                                    placeholder="Last Name"
                                    value={this.state.last_name}
                                    onChange={this.handleChange}
                                />
                                <label className="lastLabel">Last Name: </label>
                            </div>

                            <h2 className="name">Username</h2>
                            <input
                                type="text"
                                name="username"
                                required="True"
                                className="username"
                                placeholder="Username"
                                value={this.state.username}
                                onChange={this.handleChange}
                            />
                            <br/>


                        </form>
                    </div>
                </body>
            </div>
        );
    }
}

export default SignUp
